mapper.select:
SELECT

FROM	ddt_roll_book
WHERE	user_id = #{userId}

    AND name LIKE '%${queryValue}%'


    LIMIT #{offset}, #{limit}

SELECT

FROM	ddt_roll_book
WHERE	id = #{id}
		AND user_id = #{userId}

mapper.update:
UPDATE 	  ddt_roll_book
SET		  name = #{name}
		, valid_start_time = #{validStartTime}
		, valid_end_time = #{validEndTime}
		, user_count = #{userCount}
WHERE	id = #{id}
		AND user_id = #{userId}

mapper.delete:
DELETE FROM	ddt_roll_book
WHERE	id = #{rollBookId}
		AND user_id = #{userId}
